import { useEffect, useState } from "react";
import { Fraction } from "fraction.js";

const API_URL = "https://forkify-api.jonas.io/api/v2/recipes";

const wait = function (sec) {
  return new Promise(function (resolve, reject) {
    setTimeout(reject, sec * 1000);
  });
};

const transformObject = function (obj) {
  const object = Object.entries(obj).reduce((acc, [key, value]) => {
    const [first, second] = key.toLocaleLowerCase().split("_");
    //prettier-ignore
    acc[`${first}${second?.replace(second[0], second[0].toUpperCase()) ?? ""}`] = value;
    return acc;
  }, {});

  return object;
};

function App() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedId, setSelectedId] = useState(null);

  function handleSelect(id) {
    setSelectedId(id);
  }

  return (
    <div className="container">
      <Header setSearchQuery={setSearchQuery} />
      <SearchResults
        searchQuery={searchQuery}
        setSearchQuery={setSearchQuery}
        onSelect={handleSelect}
      />
      <Recipe selectedId={selectedId} />
    </div>
  );
}

function Header({ setSearchQuery }) {
  const [query, setQuery] = useState("");

  function handleSubmit(event) {
    event.preventDefault();
    setSearchQuery(query);

    setQuery("");
  }

  return (
    <header className="header">
      <img src="img/logo.png" alt="Logo" className="header__logo" />
      <form className="search" onSubmit={handleSubmit}>
        <input
          type="text"
          className="search__field"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search over 1,000,000 recipes..."
        />
        <button className="btn search__btn">
          <svg className="search__icon">
            <use href="img/icons.svg#icon-search"></use>
          </svg>
          <span>Search</span>
        </button>
      </form>

      <nav className="nav">
        <ul className="nav__list">
          <li className="nav__item">
            <button className="nav__btn nav__btn--add-recipe">
              <svg className="nav__icon">
                <use href="img/icons.svg#icon-edit"></use>
              </svg>
              <span>Add recipe</span>
            </button>
          </li>
          <li className="nav__item">
            <button className="nav__btn nav__btn--bookmarks">
              <svg className="nav__icon">
                <use href="img/icons.svg#icon-bookmark"></use>
              </svg>
              <span>Bookmarks</span>
            </button>
            <Bookmarks />
          </li>
        </ul>
      </nav>
    </header>
  );
}

function Bookmarks() {
  return (
    <div className="bookmarks">
      <ul className="bookmarks__list">
        <div className="message">
          <div>
            <svg>
              <use href="img/icons.svg#icon-smile"></use>
            </svg>
          </div>
          <p>No bookmarks yet. Find a nice recipe and bookmark it :)</p>
        </div>
      </ul>
    </div>
  );
}

const RESULTS_PER_PAGE = 10;

function SearchResults({ searchQuery, setSearchQuery, onSelect }) {
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [results, setResults] = useState([]);
  const [page, setPage] = useState(1);

  //? Derived state
  const start = (page - 1) * RESULTS_PER_PAGE;
  const end = page * RESULTS_PER_PAGE;
  const paginatedResults = results.slice(start, end);
  const numPages = Math.ceil(results.length / RESULTS_PER_PAGE);

  useEffect(
    function () {
      const controller = new AbortController();

      const fetchData = async function () {
        try {
          setIsLoading(true);
          setError("");
          setPage(1);

          const res = await fetch(`${API_URL}?search=${searchQuery}`, {
            signal: controller.signal,
          });
          const data = await res.json();

          if (!res.ok)
            throw new Error("Something went wrong with fetching data");

          if (data.data.recipes.length === 0)
            throw new Error(
              "No recipes found for your search query. Try again :)"
            );

          const recipes = data.data.recipes.map((recipe) =>
            transformObject(recipe)
          );

          setResults(recipes);
          setIsLoading(false);
          setSearchQuery("");
        } catch (err) {
          if (err.name !== "AbortError") {
            setSearchQuery("");
            setError(err.message);
            setIsLoading(false);
          }
        }
      };

      if (!searchQuery) {
        return;
      }

      fetchData();

      //? cleanup function
      return () => controller.abort();
    },
    [searchQuery, setSearchQuery]
  );

  return (
    <div className="search-results">
      {isLoading && <Spinner />}
      {error && <ErrorMessage error={error} />}
      {!error && !isLoading && results.length > 0 && (
        <>
          <ul className="results">
            {paginatedResults.map((recipe) => (
              <Item recipe={recipe} onSelect={onSelect} key={recipe.id} />
            ))}
          </ul>
          <Paginate page={page} numPages={numPages} setPage={setPage} />
        </>
      )}

      <p className="copyright">
        &copy; Copyright by{" "}
        <a className="twitter-link" target="_blank" href="#hero">
          Dushimimana Fabrice
        </a>{" "}
        {new Date().getFullYear()}. All rights reserved
      </p>
    </div>
  );
}

function Paginate({ page, numPages, setPage }) {
  return (
    <div className="pagination">
      {Array.from({ length: numPages }, (_, i) => (
        <button
          className={`pagination__btn${
            page === i + 1 ? " pagination__btn--active" : ""
          } `}
          key={i}
          onClick={() => setPage(i + 1)}
        >
          <span>{i + 1}</span>
        </button>
      ))}
    </div>
  );
}

function Item({ recipe, onSelect }) {
  function handleClick(event) {
    event.preventDefault();
    onSelect(recipe.id);
  }

  return (
    <li className="preview" onClick={handleClick}>
      <a className="preview__link" href="#fjf">
        <figure className="preview__fig">
          <img src={recipe.imageUrl} alt={recipe.title} />
        </figure>
        <div className="preview__data">
          <h4 className="preview__title">{recipe.title}</h4>
          <p className="preview__publisher">{recipe.publisher}</p>
        </div>
      </a>
    </li>
  );
}

function Recipe({ selectedId }) {
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [recipe, setRecipe] = useState({});

  //? Derived state
  const isFilled = Object.entries(recipe).length > 0;

  useEffect(
    function () {
      const fetchRecipeData = async function () {
        try {
          setIsLoading(true);
          setError("");

          const res = await fetch(`${API_URL}/${selectedId}`);
          const data = await res.json();
          const recipe = transformObject(data.data.recipe);

          setRecipe(recipe);
          setIsLoading(false);
        } catch (err) {
          setIsLoading(false);
          setError(err.message);
        }
      };

      if (!selectedId) {
        return;
      }
      fetchRecipeData();
    },
    [selectedId]
  );

  return (
    <div className="recipe">
      {isLoading && <Spinner />}
      {error && <ErrorMessage error={error} />}
      {!error && !isLoading && !isFilled && <Message />}
      {!error && !isLoading && isFilled && (
        <>
          <figure className="recipe__fig">
            <img
              src={recipe.imageUrl}
              alt={recipe.title}
              className="recipe__img"
            />
            <h1 className="recipe__title">
              <span>{recipe.title}</span>
            </h1>
          </figure>

          <div className="recipe__details">
            <div className="recipe__info">
              <svg className="recipe__info-icon">
                <use href="img/icons.svg#icon-clock"></use>
              </svg>
              <span className="recipe__info-data recipe__info-data--minutes">
                {recipe.cookingTime}
              </span>
              <span className="recipe__info-text">minutes</span>
            </div>
            <div className="recipe__info">
              <svg className="recipe__info-icon">
                <use href="img/icons.svg#icon-users"></use>
              </svg>
              <span className="recipe__info-data recipe__info-data--people">
                {recipe.servings}
              </span>
              <span className="recipe__info-text">servings</span>

              <div className="recipe__info-buttons">
                <button className="btn--tiny btn--increase-servings">
                  <svg>
                    <use href="img/icons.svg#icon-minus-circle"></use>
                  </svg>
                </button>
                <button className="btn--tiny btn--increase-servings">
                  <svg>
                    <use href="img/icons.svg#icon-plus-circle"></use>
                  </svg>
                </button>
              </div>
            </div>

            <div className="recipe__user-generated">
              <svg>
                <use href="img/icons.svg#icon-user"></use>
              </svg>
            </div>
            <button className="btn--round">
              <svg className="">
                <use href="img/icons.svg#icon-bookmark-fill"></use>
              </svg>
            </button>
          </div>

          <div className="recipe__ingredients">
            <h2 className="heading--2">Recipe ingredients</h2>
            <ul className="recipe__ingredient-list">
              {recipe.ingredients.map((ing, idx) => (
                <RecipeIngredient ing={ing} key={idx} />
              ))}
            </ul>
          </div>

          <div className="recipe__directions">
            <h2 className="heading--2">How to cook it</h2>
            <p className="recipe__directions-text">
              This recipe was carefully designed and tested by
              <span className="recipe__publisher">{recipe.publisher}</span>.
              Please check out directions at their website.
            </p>
            <a
              className="btn--small recipe__btn"
              href={recipe.sourceUrl}
              target="_blank"
              rel="noreferrer"
            >
              <span>Directions</span>
              <svg className="search__icon">
                <use href="img/icons.svg#icon-arrow-right"></use>
              </svg>
            </a>
          </div>
        </>
      )}
    </div>
  );
}

function RecipeIngredient({ ing }) {
  const quantity = new Fraction(Number(ing.quantity)).simplify().toFraction();

  return (
    <li className="recipe__ingredient">
      <svg className="recipe__icon">
        <use href="img/icons.svg#icon-check"></use>
      </svg>
      <div className="recipe__quantity">{quantity === "0" ? "" : quantity}</div>
      <div className="recipe__description">
        <span className="recipe__unit">{ing.unit} </span>
        {ing.description}
      </div>
    </li>
  );
}

function Spinner() {
  return (
    <div className="spinner">
      <svg>
        <use href="img/icons.svg#icon-loader"></use>
      </svg>
    </div>
  );
}

function Message() {
  return (
    <div className="message">
      <div>
        <svg>
          <use href="img/icons.svg#icon-smile"></use>
        </svg>
      </div>
      <p>Start by searching for a recipe or an ingredient. Have fun!</p>
    </div>
  );
}

function ErrorMessage({ error }) {
  return (
    <div className="error">
      <div>
        <svg>
          <use href="img/icons.svg#icon-alert-triangle"></use>
        </svg>
      </div>
      <p>{error}</p>
    </div>
  );
}

export default App;
