import { useEffect, useState } from "react";

const API_URL = "https://forkify-api.jonas.io/api/v2/recipes";

const wait = function (sec) {
  return new Promise(function (resolve, reject) {
    setTimeout(reject, sec * 1000);
  });
};

const transformObject = function (obj) {
  const object = Object.entries(obj).reduce((acc, [key, value]) => {
    const [first, second] = key.toLocaleLowerCase().split("_");
    //prettier-ignore
    acc[`${first}${second?.replace(second[0], second[0].toUpperCase()) ?? ""}`] = value;
    return acc;
  }, {});

  return object;
};

function App() {
  const [searchQuery, setSearchQuery] = useState("");

  return (
    <div className="container">
      <Header setSearchQuery={setSearchQuery} />
      <SearchResults
        searchQuery={searchQuery}
        setSearchQuery={setSearchQuery}
      />
      <Recipe />
    </div>
  );
}

function Header({ setSearchQuery }) {
  const [query, setQuery] = useState("");

  function handleSubmit(event) {
    event.preventDefault();
    setSearchQuery(query);

    console.log(query);

    setQuery("");
  }

  return (
    <header className="header">
      <img src="img/logo.png" alt="Logo" className="header__logo" />
      <form className="search" onSubmit={handleSubmit}>
        <input
          type="text"
          className="search__field"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search over 1,000,000 recipes..."
        />
        <button className="btn search__btn">
          <svg className="search__icon">
            <use href="img/icons.svg#icon-search"></use>
          </svg>
          <span>Search</span>
        </button>
      </form>

      <nav className="nav">
        <ul className="nav__list">
          <li className="nav__item">
            <button className="nav__btn nav__btn--add-recipe">
              <svg className="nav__icon">
                <use href="img/icons.svg#icon-edit"></use>
              </svg>
              <span>Add recipe</span>
            </button>
          </li>
          <li className="nav__item">
            <button className="nav__btn nav__btn--bookmarks">
              <svg className="nav__icon">
                <use href="img/icons.svg#icon-bookmark"></use>
              </svg>
              <span>Bookmarks</span>
            </button>
            <div className="bookmarks">
              <ul className="bookmarks__list">
                <div className="message">
                  <div>
                    <svg>
                      <use href="img/icons.svg#icon-smile"></use>
                    </svg>
                  </div>
                  <p>No bookmarks yet. Find a nice recipe and bookmark it :)</p>
                </div>

                <li className="preview">
                  <a className="preview__link" href="#23456">
                    <figure className="preview__fig">
                      <img src="img/test-1.jpg" alt="Test" />
                    </figure>
                    <div className="preview__data">
                      <h4 className="preview__name">
                        Pasta with Tomato Cream ...
                      </h4>
                      <p className="preview__publisher">The Pioneer Woman</p>
                    </div>
                  </a>
                </li>
              </ul>
            </div>
          </li>
        </ul>
      </nav>
    </header>
  );
}

function SearchResults({ searchQuery, setSearchQuery }) {
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [results, setResults] = useState([]);
  console.log(searchQuery);

  useEffect(
    function () {
      const controller = new AbortController();

      const fetchData = async function () {
        try {
          setIsLoading(true);
          setError("");

          const res = await fetch(`${API_URL}?search=${searchQuery}`, {
            signal: controller.signal,
          });
          const data = await res.json();

          if (!res.ok)
            throw new Error("Something went wrong with fetching data");

          const recipes = data.data.recipes.map((recipe) =>
            transformObject(recipe)
          );
          setResults(recipes);

          setIsLoading(false);
        } catch (err) {
          if (err.name !== "AbortError") {
            setSearchQuery("");
            setError(err.message);
            setIsLoading(false);
          }
        } finally {
        }
      };

      if (!searchQuery) {
        return;
      }

      fetchData();

      //? cleanup function
      return () => controller.abort();
    },
    [searchQuery]
  );

  return (
    <div className="search-results">
      {isLoading && <Spinner />}
      {error && <ErrorMessage error={error} />}
      {!error && !isLoading && results.length > 0 && (
        <>
          <ul className="results">
            <li className="preview">
              <a className="preview__link preview__link--active" href="#23456">
                <figure className="preview__fig">
                  <img src="img/test-1.jpg" alt="Test" />
                </figure>
                <div className="preview__data">
                  <h4 className="preview__title">
                    Pasta with Tomato Cream ...
                  </h4>
                  <p className="preview__publisher">The Pioneer Woman</p>
                  <div className="preview__user-generated">
                    <svg>
                      <use href="img/icons.svg#icon-user"></use>
                    </svg>
                  </div>
                </div>
              </a>
            </li>
          </ul>
          <div className="pagination">
            <button className="btn--inline pagination__btn--prev">
              <svg className="search__icon">
                <use href="img/icons.svg#icon-arrow-left"></use>
              </svg>
              <span>Page 1</span>
            </button>
            <button className="btn--inline pagination__btn--next">
              <span>Page 3</span>
              <svg className="search__icon">
                <use href="img/icons.svg#icon-arrow-right"></use>
              </svg>
            </button>
          </div>
        </>
      )}

      <p className="copyright">
        &copy; Copyright by{" "}
        <a className="twitter-link" target="_blank" href="#hero">
          Dushimimana Fabrice
        </a>{" "}
        {new Date().getFullYear()}. All rights reserved
      </p>
    </div>
  );
}

function Recipe() {
  return (
    <div className="recipe">
      <figure className="recipe__fig">
        <img src="img/test-1.jpg" alt="Tomato" className="recipe__img" />
        <h1 className="recipe__title">
          <span>Pasta with tomato cream sauce</span>
        </h1>
      </figure>

      <div className="recipe__details">
        <div className="recipe__info">
          <svg className="recipe__info-icon">
            <use href="img/icons.svg#icon-clock"></use>
          </svg>
          <span className="recipe__info-data recipe__info-data--minutes">
            45
          </span>
          <span className="recipe__info-text">minutes</span>
        </div>
        <div className="recipe__info">
          <svg className="recipe__info-icon">
            <use href="img/icons.svg#icon-users"></use>
          </svg>
          <span className="recipe__info-data recipe__info-data--people">4</span>
          <span className="recipe__info-text">servings</span>

          <div className="recipe__info-buttons">
            <button className="btn--tiny btn--increase-servings">
              <svg>
                <use href="img/icons.svg#icon-minus-circle"></use>
              </svg>
            </button>
            <button className="btn--tiny btn--increase-servings">
              <svg>
                <use href="img/icons.svg#icon-plus-circle"></use>
              </svg>
            </button>
          </div>
        </div>

        <div className="recipe__user-generated">
          <svg>
            <use href="img/icons.svg#icon-user"></use>
          </svg>
        </div>
        <button className="btn--round">
          <svg className="">
            <use href="img/icons.svg#icon-bookmark-fill"></use>
          </svg>
        </button>
      </div>

      <div className="recipe__ingredients">
        <h2 className="heading--2">Recipe ingredients</h2>
        <ul className="recipe__ingredient-list">
          <li className="recipe__ingredient">
            <svg className="recipe__icon">
              <use href="img/icons.svg#icon-check"></use>
            </svg>
            <div className="recipe__quantity">1000</div>
            <div className="recipe__description">
              <span className="recipe__unit">g</span>
              pasta
            </div>
          </li>

          <li className="recipe__ingredient">
            <svg className="recipe__icon">
              <use href="img/icons.svg#icon-check"></use>
            </svg>
            <div className="recipe__quantity">0.5</div>
            <div className="recipe__description">
              <span className="recipe__unit">cup</span>
              ricotta cheese
            </div>
          </li>
        </ul>
      </div>

      <div className="recipe__directions">
        <h2 className="heading--2">How to cook it</h2>
        <p className="recipe__directions-text">
          This recipe was carefully designed and tested by
          <span className="recipe__publisher">The Pioneer Woman</span>. Please
          check out directions at their website.
        </p>
        <a
          className="btn--small recipe__btn"
          href="http://thepioneerwoman.com/cooking/pasta-with-tomato-cream-sauce/"
          target="_blank"
        >
          <span>Directions</span>
          <svg className="search__icon">
            <use href="img/icons.svg#icon-arrow-right"></use>
          </svg>
        </a>
      </div>
    </div>
  );
}

function Spinner() {
  return (
    <div className="spinner">
      <svg>
        <use href="img/icons.svg#icon-loader"></use>
      </svg>
    </div>
  );
}

function Message() {
  return (
    <div className="message">
      <div>
        <svg>
          <use href="img/icons.svg#icon-smile"></use>
        </svg>
      </div>
      <p>Start by searching for a recipe or an ingredient. Have fun!</p>
    </div>
  );
}

function ErrorMessage({ error }) {
  return (
    <div className="error">
      <div>
        <svg>
          <use href="img/icons.svg#icon-alert-triangle"></use>
        </svg>
      </div>
      <p>{error}</p>
    </div>
  );
}

export default App;
